","keccak256":"0x52a8cfb0f5239d11b457dcdd1b326992ef672714ca8da71a157255bddd13f3ad","license":"MIT"},"contracts/MessageLibBase.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";

import "./interfaces/ILayerZeroExecutor.sol";
import "./interfaces/ILayerZeroTreasury.sol";

struct WorkerOptions {
    uint8 workerId;
    bytes options;
}

enum DeliveryState {
    Signing,
    Deliverable,
    Delivered,
    Waiting
}

abstract contract MessageLibBase is Ownable {
    address internal immutable endpoint;
    uint32 internal immutable localEid;
    uint8 internal immutable packetVersion;
    uint internal immutable treasuryGasCap;

    // config
    address public treasury;
    mapping(uint32 dstEid => bytes option) public defaultOptions;

    // accumulated fees for workers and treasury
    mapping(address worker => uint) public fees;

    event ExecutorFeePaid(address executor, uint fee);

    // only the endpoint can call SEND() and setConfig()
    modifier onlyEndpoint() {
        require(endpoint == msg.sender, Errors.PERMISSION_DENIED);
        _;
    }

    constructor(address _endpoint, uint32 _localEid, uint8 _packetVersion, uint _treasuryGasCap) {
        endpoint = _endpoint;
        localEid = _localEid;
        packetVersion = _packetVersion;
        treasuryGasCap = _treasuryGasCap;
    }

    // ======================= OnlyOwner =======================
    function setDefaultOptions(uint32 _dstEid, bytes calldata _options) external onlyOwner {
        defaultOptions[_dstEid] = _options;
    }

    // ======================= Internal =======================
    function _assertMessageSize(uint _actual, uint _max) internal pure {
        require(_actual <= _max, Errors.INVALID_SIZE);
    }

    function _sendToExecutor(
        address _executor,
        uint32 _dstEid,
        address _sender,
        uint _msgSize,
        bytes memory _executorOptions
    ) internal returns (uint executorFee) {
        executorFee = ILayerZeroExecutor(_executor).assignJob(_dstEid, _sender, _msgSize, _executorOptions);
        if (executorFee > 0) {
            fees[_executor] += executorFee;
        }
        emit ExecutorFeePaid(_executor, executorFee);
    }

    function _sendToTreasury(
        address _sender,
        uint32 _dstEid,
        uint _totalNativeFee,
        bool _payInLzToken
    ) internal returns (uint treasuryNativeFee, uint lzTokenFee) {
        // fee should be in lzTokenFee if payInLzToken, otherwise in native
        (treasuryNativeFee, lzTokenFee) = _quoteTreasuryFee(_sender, _dstEid, _totalNativeFee, _payInLzToken);
        // if payInLzToken, handle in messagelib / endpoint
        if (treasuryNativeFee > 0) {
            fees[treasury] += treasuryNativeFee;
        }
    }

    function _getWorkerOptions(
        uint32 _dstEid,
        bytes calldata _options
    )
        internal
        view
        returns (bytes memory options, bytes memory executorOptions, WorkerOptions[] memory otherWorkerOptions)
    {
        // use default if no option provided
        if (_options.length == 0) {
            options = defaultOptions[_dstEid];
        } else {
            options = _options;
        }

        // split options by worker type
        if (options.length > 0) (executorOptions, otherWorkerOptions) = _getExecutorOptions(options);
    }

    function _quote(
        address _sender,
        uint32 _dstEid,
        uint _msgSize,
        bool _payInLzToken,
        bytes calldata _options
    ) internal view returns (uint, uint) {
        (, bytes memory executorOptions, WorkerOptions[] memory otherWorkerOptions) = _getWorkerOptions(
            _dstEid,
            _options
        );

        // quote other workers
        (uint nativeFee, address executor, uint maxMsgSize) = _quoteWorkers(_sender, _dstEid, otherWorkerOptions);

        // assert msg size
        _assertMessageSize(_msgSize, maxMsgSize);

        // quote executor
        nativeFee += ILayerZeroExecutor(executor).getFee(_dstEid, _sender, _msgSize, executorOptions);

        // quote treasury
        (uint treasuryNativeFee, uint lzTokenFee) = _quoteTreasuryFee(_sender, _dstEid, nativeFee, _payInLzToken);
        if (treasuryNativeFee > 0) {
            nativeFee += treasuryNativeFee;
        }

        return (nativeFee, lzTokenFee);
    }

    function _quoteTreasuryFee(
        address _sender,
        uint32 _eid,
        uint _totalFee,
        bool _payInLzToken
    ) internal view returns (uint nativeFee, uint lzTokenFee) {
        if (treasury != address(0x0)) {
            try ILayerZeroTreasury(treasury).getFee(_sender, _eid, _totalFee, _payInLzToken) returns (
                uint treasuryFee
            ) {
                // success
                if (_payInLzToken) {
                    lzTokenFee = treasuryFee;
                } else {
                    // pay in native, make sure that the treasury fee is not higher than the cap
                    uint gasFeeEstimate = tx.gasprice * treasuryGasCap;
                    // cap is the max of total fee and gasFeeEstimate. this is to prevent apps from forcing the cap to 0.
                    uint nativeFeeCap = _totalFee > gasFeeEstimate ? _totalFee : gasFeeEstimate;
                    // to prevent the treasury from returning an overly high value to break the path
                    nativeFee = treasuryFee > nativeFeeCap ? nativeFeeCap : treasuryFee;
                }
            } catch {
                // failure, something wrong with treasury contract, charge nothing and continue
            }
        }
    }

    function _transferNative(address _to, uint _amount) internal {
        (bool success, ) = _to.call{value: _amount}("");
        require(success, Errors.INVALID_STATE);
    }

    // for msg.sender only
    function _assertAndDebitAmount(address _to, uint _amount) internal {
        require(_to != address(0x0) && _amount <= fees[msg.sender], Errors.INVALID_ARGUMENT);
        fees[msg.sender] -= _amount;
    }

    // ======================= Virtual =======================
    // For implementation to override
    function _quoteWorkers(
        address _oapp,
        uint32 _eid,
        WorkerOptions[] memory _options
    ) internal view virtual returns (uint, address, uint);

    function _getExecutorOptions(
        bytes memory _options
    ) internal view virtual returns (bytes memory executorOptions, WorkerOptions[] memory otherWorkerOptions);
}
","keccak256":"0xae762395ed46d89e1cf69abab0b92f5ddbd220d4aa7afdb5249f33977e15178e","license":"BUSL-1.1"},"contracts/OutboundConfig.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.19;

import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";
import "./interfaces/ILayerZeroExecutor.sol";

struct OutboundConfigStruct {
    uint32 maxMessageSize;
    uint64 outboundConfirmations;
    address executor;
}

/// extract it because it might be reused by future non-ULN msglibs
contract OutboundConfig {
    // Derived MessageLibs should never use this as config type
    uint32 internal constant CONFIG_TYPE_MAX_MESSAGE_SIZE = 1;
    uint32 internal constant CONFIG_TYPE_OUTBOUND_CONFIRMATIONS = 2;
    uint32 internal constant CONFIG_TYPE_EXECUTOR = 3;

    mapping(uint32 dstEid => OutboundConfigStruct) public defaultOutboundConfig;
    mapping(address oapp => mapping(uint32 dstEid => OutboundConfigStruct)) public outboundConfig;

    // ============================ View ===================================
    function getOutboundConfigByType(
        uint32 _eid,
        address _oapp,
        uint _configType
    ) public view returns (bytes memory, bool) {
        if (_configType == CONFIG_TYPE_MAX_MESSAGE_SIZE) {
            OutboundConfigStruct storage config = outboundConfig[_oapp][_eid];
            if (config.maxMessageSize == 0) {
                OutboundConfigStruct storage defaultConfig = defaultOutboundConfig[_eid];
                return (abi.encode(defaultConfig.maxMessageSize), true);
            }
            return (abi.encode(config.maxMessageSize), false);
        } else if (_configType == CONFIG_TYPE_OUTBOUND_CONFIRMATIONS) {
            OutboundConfigStruct storage config = outboundConfig[_oapp][_eid];
            if (config.outboundConfirmations == 0) {
                OutboundConfigStruct storage defaultConfig = defaultOutboundConfig[_eid];
                return (abi.encode(defaultConfig.outboundConfirmations), true);
            }
            return (abi.encode(config.outboundConfirmations), false);
        } else if (_configType == CONFIG_TYPE_EXECUTOR) {
            OutboundConfigStruct storage config = outboundConfig[_oapp][_eid];
            if (config.executor == address(0x0)) {
                OutboundConfigStruct storage defaultConfig = defaultOutboundConfig[_eid];
                return (abi.encode(defaultConfig.executor), true);
            }
            return (abi.encode(config.executor), false);
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
    }

    function getDefaultOutboundConfigByType(uint32 _eid, uint32 _configType) public view returns (bytes memory) {
        if (_configType == CONFIG_TYPE_MAX_MESSAGE_SIZE) {
            OutboundConfigStruct storage config = defaultOutboundConfig[_eid];
            return abi.encode(config.maxMessageSize);
        } else if (_configType == CONFIG_TYPE_OUTBOUND_CONFIRMATIONS) {
            OutboundConfigStruct storage config = defaultOutboundConfig[_eid];
            return abi.encode(config.outboundConfirmations);
        } else if (_configType == CONFIG_TYPE_EXECUTOR) {
            OutboundConfigStruct storage config = defaultOutboundConfig[_eid];
            return abi.encode(config.executor);
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
    }

    function getOutboundConfig(address _oapp, uint32 _dstEid) public view returns (OutboundConfigStruct memory) {
        OutboundConfigStruct memory config = outboundConfig[_oapp][_dstEid];
        OutboundConfigStruct memory defaultConfig = defaultOutboundConfig[_dstEid];
        require(defaultConfig.maxMessageSize > 0, Errors.INVALID_EID); // available remote eid

        if (config.maxMessageSize == 0) {
            config.maxMessageSize = defaultConfig.maxMessageSize;
        }
        if (config.outboundConfirmations == 0) {
            config.outboundConfirmations = defaultConfig.outboundConfirmations;
        }
        if (config.executor == address(0x0)) {
            config.executor = defaultConfig.executor;
        }
        return config;
    }

    // ============================ Internal ===================================
    function _setDefaultOutboundConfig(uint32 _eid, OutboundConfigStruct calldata _config) internal {
        require(_config.outboundConfirmations > 0 && _config.executor != address(0x0), Errors.INVALID_ARGUMENT);
        require(_config.maxMessageSize > 0, Errors.INVALID_SIZE);

        OutboundConfigStruct storage config = defaultOutboundConfig[_eid];
        config.executor = _config.executor;
        config.maxMessageSize = _config.maxMessageSize;
        config.outboundConfirmations = _config.outboundConfirmations;
    }

    function _setOutboundConfigByType(address _oapp, uint32 _eid, uint32 _configType, bytes calldata _config) internal {
        OutboundConfigStruct storage config = outboundConfig[_oapp][_eid];
        if (_configType == CONFIG_TYPE_MAX_MESSAGE_SIZE) {
            uint32 maxMessageSize = abi.decode(_config, (uint32));
            config.maxMessageSize = maxMessageSize;
        } else if (_configType == CONFIG_TYPE_OUTBOUND_CONFIRMATIONS) {
            uint64 outboundConfirmations = abi.decode(_config, (uint64));
            config.outboundConfirmations = outboundConfirmations;
        } else if (_configType == CONFIG_TYPE_EXECUTOR) {
            address executor = abi.decode(_config, (address));
            config.executor = executor;
        } else {
            revert(Errors.NOT_IMPLEMENTED);
        }
    }

    function _snapshotOutboundConfig(uint32 _eid, address _oapp) internal {
        outboundConfig[_oapp][_eid] = getOutboundConfig(_oapp, _eid);
    }

    function _resetOutboundConfig(uint32 _eid, address _oapp) internal {
        delete outboundConfig[_oapp][_eid];
    }
}
