pragma solidity ^0.8.0;

import "solidity-bytes-utils/contracts/BytesLib.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/BitMaps.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/CalldataBytesLib.sol";

library VerifierOptions {
    using BitMaps for BitMaps.BitMap256;
    using CalldataBytesLib for bytes;
    using BytesLib for bytes;

    uint8 internal constant WORKER_ID = 2;
    uint8 internal constant OPTION_TYPE_PRECRIME = 1;

    /// @dev group verifier options by its idx
    /// @param _options [verifier_id][verifier_option][verifier_id][verifier_option]...
    ///        verifier_option = [option_size][verifier_idx][option_type][option]
    ///        option_size = len(verifier_idx) + len(option_type) + len(option)
    ///        verifier_id: uint8, verifier_idx: uint8, option_size: uint16, option_type: uint8, option: bytes
    /// @return verifierOptions the grouped options, still share the same format of _options
    /// @return verifierIndices the verifier indices
    function groupVerifierOptionsByIdx(
        bytes memory _options
    ) internal pure returns (bytes[] memory verifierOptions, uint8[] memory verifierIndices) {
        if (_options.length == 0) return (verifierOptions, verifierIndices);

        uint8 numVerifiers = getNumVerifiers(_options);

        // if there is only 1 verifier, we can just return the whole options
        if (numVerifiers == 1) {
            verifierOptions = new bytes[](1);
            verifierOptions[0] = _options;

            verifierIndices = new uint8[](1);
            verifierIndices[0] = _options.toUint8(3); // verifier idx
            return (verifierOptions, verifierIndices);
        }

        // otherwise, we need to group the options by verifier_idx
        verifierIndices = new uint8[](numVerifiers);
        verifierOptions = new bytes[](numVerifiers);
        unchecked {
            uint cursor;
            uint start;
            uint8 lastVerifierIdx = 255; // 255 is an invalid verifier_idx

            while (cursor < _options.length) {
                ++cursor; // skip worker_id

                uint16 optionLength = _options.toUint16(cursor);
                cursor += 2;
                require(optionLength >= 2, Errors.INVALID_SIZE); // at least 1 byte for option_type and 1 byte for verifier_idx

                uint8 verifierIdx = _options.toUint8(cursor);

                if (lastVerifierIdx == 255) {
                    lastVerifierIdx = verifierIdx;
                }

                // verifierIdx must equal to the lastVerifierIdx for the first option
                // so it is always skipped in the first option
                // this operation slices out options whenever the the scan finds a different lastVerifierIdx
                if (verifierIdx != lastVerifierIdx) {
                    uint size = cursor - start - 3; // 3 is for worker_id and option_length
                    bytes memory op = _options.slice(start, size);
                    _insertVerifierOptions(verifierOptions, verifierIndices, lastVerifierIdx, op);

                    // reset the start and lastVerifierIdx
                    start += size;
                    lastVerifierIdx = verifierIdx;
                }

                cursor += optionLength;

                // if we have reached the end of the options, we need to process the last verifier
                if (cursor == _options.length) {
                    uint size = cursor - start;
                    bytes memory op = _options.slice(start, size);
                    _insertVerifierOptions(verifierOptions, verifierIndices, verifierIdx, op);
                }
            }
            require(cursor == _options.length, Errors.INVALID_SIZE);

            // revert verifierIndices to start from 0
            for (uint8 i = 0; i < numVerifiers; ++i) {
                --verifierIndices[i];
            }
        }
    }

    function _insertVerifierOptions(
        bytes[] memory _verifierOptions,
        uint8[] memory _verifierIndices,
        uint8 _verifierIdx,
        bytes memory _newOptions
    ) internal pure {
        // verifierIdx starts from 0 but default value of verifierIndices is 0, so we tell if the slot is empty by adding 1 to verifierIdx
        require(_verifierIdx < 255, Errors.INVALID_VERIFIERS);
        uint8 verifierIdxAdj = _verifierIdx + 1;

        for (uint8 j = 0; j < _verifierIndices.length; ++j) {
            uint8 index = _verifierIndices[j];
            if (verifierIdxAdj == index) {
                _verifierOptions[j] = abi.encodePacked(_verifierOptions[j], _newOptions);
                break;
            } else if (index == 0) {
                // empty slot, that means it is the first time we see this verifier
                _verifierIndices[j] = verifierIdxAdj;
                _verifierOptions[j] = _newOptions;
                break;
            }
        }
    }

    /// @dev get the number of unique verifiers
    /// @param _options the format is the same as groupVerifierOptionsByIdx
    function getNumVerifiers(bytes memory _options) internal pure returns (uint8 numVerifiers) {
        uint cursor;
        BitMaps.BitMap256 bitmap;

        // find number of unique verifier_idx
        unchecked {
            while (cursor < _options.length) {
                ++cursor; // skip worker_id

                uint16 optionLength = _options.toUint16(cursor);
                cursor += 2;
                require(optionLength >= 2, Errors.INVALID_SIZE); // at least 1 byte for verifier_idx and 1 byte for option_type

                uint8 verifierIdx = _options.toUint8(cursor);

                // verifierIdx starts from 0, add one for bitmap check/set
                require(verifierIdx < 255, Errors.INVALID_VERIFIERS);
                uint8 verifierIdxAdj = verifierIdx + 1;

                // if verifierIdx is not set, increment numVerifiers
                if (!bitmap.get(verifierIdxAdj)) {
                    ++numVerifiers;
                    bitmap = bitmap.set(verifierIdxAdj);
                }

                cursor += optionLength;
            }
        }
        require(cursor == _options.length, Errors.INVALID_SIZE);
    }

    /// @dev decode the next verifier option from _options starting from the specified cursor
    /// @param _options the format is the same as groupVerifierOptionsByIdx
    /// @param _cursor the cursor to start decoding
    /// @return optionType the type of the option
    /// @return option the option
    /// @return cursor the cursor to start decoding the next option
    function nextVerifierOption(
        bytes calldata _options,
        uint _cursor
    ) internal pure returns (uint8 optionType, bytes calldata option, uint cursor) {
        unchecked {
            // skip worker id
            cursor = _cursor + 1;

            // read option size
            uint16 size = _options.toU16(cursor);
            cursor += 2;

            // read option type
            optionType = _options.toU8(cursor + 1); // skip verifier_idx

            // startCursor and endCursor are used to slice the option from _options
            uint startCursor = cursor + 2; // skip option type and verifier_idx
            uint endCursor = cursor + size;
            option = _options[startCursor:endCursor];
            cursor += size;
        }
    }
}
","keccak256":"0x64ce5ceb89a26cdcddd34f2d90838cc41de88beb510c810247648de085492b31","license":"BUSL-1.1"},"contracts/uln/uln301/MessageLibBaseE1.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {Origin} from "@layerzerolabs/lz-evm-protocol-v2/contracts/MessagingStructs.sol";
import "@layerzerolabs/lz-evm-v1-0.7/contracts/interfaces/ILayerZeroEndpoint.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/Errors.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/AddressCast.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/ExecutorOptions.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/PacketV1Codec.sol";

import "./interfaces/IMessageLibE1.sol";
import "./interfaces/ITreasuryFeeHandler.sol";
import "./NonceContract.sol";

import "../../interfaces/ILayerZeroExecutor.sol";
import "../../MessageLibBase.sol";

/// @dev base contract for msglib on endpoint v1. It enforces the path convention
abstract contract MessageLibBaseE1 is MessageLibBase, IMessageLibE1 {
    using PacketV1Codec for bytes;
    using AddressCast for address;
    using AddressCast for bytes32;

    NonceContract internal immutable nonceContract;
    address internal immutable treasuryFeeHandler;

    // config
    address internal layerZeroToken;
    // EndpointV1 is using bytes as address. this map is for address length assertion
    mapping(uint32 dstEid => uint size) public addressSizes;

    // this event is the same as the PacketReceived event on EndpointV2
    event PacketReceived(Origin origin, address receiver);
    // this event should be identical to the one on Endpoint V2
    event PacketSent(bytes encodedPayload, bytes options, uint nativeFee, uint lzTokenFee);
    event NativeFeeWithdrawn(address user, address receiver, uint amount);
    event InvalidDst(
        uint16 indexed srcChainId,
        bytes32 srcAddress,
        address indexed dstAddress,
        uint64 nonce,
        bytes32 payloadHash
    );

    constructor(
        address _endpoint,
        uint8 _packetVersion,
        uint _treasuryGasCap,
        address _nonceContract,
        uint32 _localEid,
        address _treasuryFeeHandler
    ) MessageLibBase(_endpoint, _localEid, _packetVersion, _treasuryGasCap) {
        nonceContract = NonceContract(_nonceContract);
        treasuryFeeHandler = _treasuryFeeHandler;
    }

    // ======================= OnlyEndpoint =======================
    function send(
        address _sender,
        uint64, // _nonce
        uint16 _dstEid,
        bytes calldata _path, // remoteAddress + localAddress
        bytes calldata _message,
        address payable _refundAddress,
        address _lzTokenPaymentAddress,
        bytes calldata _options
    ) external payable onlyEndpoint {
        (bytes memory options, bytes memory encodedPacket, uint totalNativeFee) = _sendToWorkers(
            _sender,
            _dstEid,
            _path,
            _message,
            _options
        );

        // quote treasury fee
        uint32 dstEid = _dstEid; // stack too deep
        address sender = _sender; // stack too deep
        bool payInLzToken = _lzTokenPaymentAddress != address(0x0) && address(layerZeroToken) != address(0x0);
        (uint treasuryNativeFee, uint lzTokenFee) = _sendToTreasury(sender, dstEid, totalNativeFee, payInLzToken);
        totalNativeFee += treasuryNativeFee;

        // pay native fee
        // assert the user has attached enough native token for this address
        require(totalNativeFee <= msg.value, Errors.INVALID_AMOUNT);
        // refund if they send too much
        uint refundAmt = msg.value - totalNativeFee;
        if (refundAmt > 0) {
            _transferNative(_refundAddress, refundAmt);
        }

        // pay lz token fee if needed
        if (lzTokenFee > 0) {
            _payLzTokenFee(sender, _lzTokenPaymentAddress, lzTokenFee);
        }

        emit PacketSent(encodedPacket, options, totalNativeFee, lzTokenFee);
    }

    // ======================= OnlyOwner =======================
    function setLayerZeroToken(address _layerZeroToken) external onlyOwner {
        layerZeroToken = _layerZeroToken;
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
    }

    function setAddressSize(uint16 _eid, uint _size) external onlyOwner {
        require(_size <= 32, Errors.INVALID_SIZE);
        require(addressSizes[_eid] == 0, Errors.ALREADY_EXISTS);
        addressSizes[_eid] = _size;
    }

    // ======================= External =======================
    function withdrawFee(address _to, uint _amount) external {
        _assertAndDebitAmount(_to, _amount);
        _transferNative(_to, _amount);
        emit NativeFeeWithdrawn(msg.sender, _to, _amount);
    }

    // ======================= View =======================
    function estimateFees(
        uint16 _dstEid,
        address _sender,
        bytes calldata _message,
        bool _payInLzToken,
        bytes calldata _options
    ) external view returns (uint nativeFee, uint zroFee) {
        return _quote(_sender, _dstEid, _message.length, _payInLzToken, _options);
    }

    // ======================= Internal =======================
    /// @dev path = remoteAddress + localAddress.
    function _assertPath(address _sender, bytes calldata _path, uint remoteAddressSize) internal pure {
        require(_path.length == 20 + remoteAddressSize, Errors.INVALID_ARGUMENT);
        address srcInPath = AddressCast.toAddress(_path[remoteAddressSize:]);
        require(_sender == srcInPath, Errors.INVALID_ARGUMENT);
    }

    function _payLzTokenFee(address _sender, address _lzTokenPaymentAddress, uint _lzTokenFee) internal {
        ITreasuryFeeHandler(treasuryFeeHandler).payFee(
            layerZeroToken,
            _sender,
            _lzTokenPaymentAddress,
            _lzTokenFee, // the supplied fee is always equal to the required fee
            _lzTokenFee,
            treasury
        );
    }

    /// @dev outbound does three things
    /// @dev 1) asserts path
    /// @dev 2) increments the nonce
    /// @dev 3) assemble packet
    /// @return packet to be sent to workers
    function _outbound(
        address _sender,
        uint16 _dstEid,
        bytes calldata _path,
        bytes calldata _message
    ) internal returns (Packet memory packet) {
        // assert toAddress size
        uint remoteAddressSize = addressSizes[_dstEid];
        _assertPath(_sender, _path, remoteAddressSize);

        // increment nonce
        uint64 nonce = nonceContract.increment(_dstEid, _sender, _path);

        bytes32 receiver = AddressCast.toBytes32(_path[0:remoteAddressSize]);

        // todo: should use the PacketV1Codec?
        bytes32 guid = keccak256(abi.encodePacked(nonce, localEid, _sender.toBytes32(), uint32(_dstEid), receiver));

        // assemble packet
        packet = Packet(nonce, localEid, _sender, _dstEid, receiver, guid, _message);
    }

    function _sendToWorkers(
        address _sender,
        uint16 _dstEid,
        bytes calldata _path,
        bytes calldata _message,
        bytes calldata _options
    ) internal returns (bytes memory options, bytes memory encodedPacket, uint totalNativeFee) {
        Packet memory packet = _outbound(_sender, _dstEid, _path, _message);
        encodedPacket = PacketV1Codec.encode2(packetVersion, packet);

        bytes memory executorOptions;
        WorkerOptions[] memory otherWorkerOptions;
        (options, executorOptions, otherWorkerOptions) = _getWorkerOptions(packet.dstEid, _options);

        (uint otherWorkerFees, address executor, uint maxMsgSize) = _send(packet, otherWorkerOptions);
        totalNativeFee += otherWorkerFees;

        uint msgSize = packet.message.length;
        _assertMessageSize(msgSize, maxMsgSize);

        totalNativeFee += _sendToExecutor(executor, packet.dstEid, packet.sender, msgSize, executorOptions);
    }

    /// @dev this function change pack the path as required for EndpointV1
    function _execute(
        uint16 _srcEid,
        bytes32 _sender,
        address _receiver,
        uint64 _nonce,
        bytes memory _message,
        uint _gasLimit
    ) internal {
        if (_receiver.code.length == 0) {
            /// on chains where EOA has no codes, we will early return and emit InvalidDst event because it will result into a revert without msg ( call to non-contract account).
            // on chains where all address have codes, this will be skipped
            emit InvalidDst(_srcEid, _sender, _receiver, _nonce, keccak256(_message));
            return;
        }

        bytes memory pathData = abi.encodePacked(_sender.toBytes(addressSizes[_srcEid]), _receiver);
        ILayerZeroEndpoint(endpoint).receivePayload(_srcEid, pathData, _receiver, _nonce, _gasLimit, _message);

        Origin memory origin = Origin(_srcEid, _sender, _nonce);
        emit PacketReceived(origin, _receiver);
    }

    function _endpointDeliverable(
        uint16 _srcEid,
        address _receiver,
        bytes calldata _packetHeader
    ) internal view returns (DeliveryState) {
        ILayerZeroEndpoint lzEndpoint = ILayerZeroEndpoint(endpoint);

        bytes memory sender = _packetHeader.sender().toBytes(addressSizes[_srcEid]);
        bytes memory path = abi.encodePacked(sender, _receiver);
        uint64 nonce = _packetHeader.nonce();

        // assert message library
        require(lzEndpoint.getReceiveLibraryAddress(_receiver) == address(this), Errors.PERMISSION_DENIED);

        // 1. check if nonce is already delivered
        uint64 inboundNonce = lzEndpoint.getInboundNonce(_srcEid, path);
        if (nonce <= inboundNonce) return DeliveryState.Delivered;

        // 2. check nonce is next nonce
        // 3. check there are no stored payload
        if (nonce > inboundNonce + 1 || lzEndpoint.hasStoredPayload(_srcEid, path)) return DeliveryState.Waiting;

        return DeliveryState.Deliverable;
    }

    // ======================= Virtual =======================
    // For implementation to override
    // packet is assembled in memory for endpoint-v1
    function _send(
        Packet memory _packet,
        WorkerOptions[] memory _options
    ) internal virtual returns (uint, address, uint);
}
","keccak256":"0x54a339dc4ef416f52c1a5d053340d9dd2b7e716ca705759827708d5d31cd7ccf","license":"BUSL-1.1"},"contracts/uln/uln301/NonceContract.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.19;

import "@layerzerolabs/lz-evm-v1-0.7/contracts/interfaces/ILayerZeroEndpoint.sol";

contract NonceContract {
    ILayerZeroEndpoint public immutable endpoint;
    mapping(uint16 dstEid => mapping(bytes path => uint64 nonce)) public outboundNonce;

    constructor(address _endpoint) {
        endpoint = ILayerZeroEndpoint(_endpoint);
    }

    function increment(uint16 _chainId, address _ua, bytes calldata _path) external returns (uint64) {
        require(
            endpoint.getSendLibraryAddress(_ua) == msg.sender,
            "NonceContract: msg.sender is not valid sendlibrary"
        );
        return ++outboundNonce[_chainId][_path];
    }
}
","keccak256":"0x2bc18b0b5e44ff149a2a20b164123b7ec1f51a8f670ec8f224c0be0bc847d8e6","license":"BUSL-1.1"},"contracts/uln/uln301/UltraLightNode301.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/libs/AddressCast.sol";
import "@layerzerolabs/lz-evm-v1-0.7/contracts/interfaces/ILayerZeroEndpoint.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/PacketV1Codec.sol";

import "./interfaces/IUltraLightNode301.sol";
import {UlnConfigStruct} from "../interfaces/IUlnConfig.sol";
import "../libs/UlnOptions.sol";

import "./MessageLibBaseE1.sol";
import "../UlnBase.sol";

/// @dev ULN301 will be deployed on EndpointV1 and is for backward compatability with ULN302 on EndpointV2. 301 can talk to both 301 and 302
contract UltraLightNode301 is IUltraLightNode301, UlnBase, MessageLibBaseE1 {
    using PacketV1Codec for bytes;
    using SafeCast for uint32; // for chain ID uint16 to uint32 conversion
    using AddressCast for bytes32; // for address type bytes to bytes32/address conversion

    constructor(
        address _endpoint,
        uint _treasuryGasCap,
        address _nonceContract,
        uint32 _localEid,
        address _treasuryFeeHandler,
        address _ulnConfig
    )
        MessageLibBaseE1(_endpoint, 1, _treasuryGasCap, _nonceContract, _localEid, _treasuryFeeHandler)
        UlnBase(_ulnConfig)
    {}

    // ============================ OnlyEndpoint ===================================

    function setConfig(
        uint16 _eid,
        address _oapp,
        uint _configType,
        bytes calldata _config
    ) external override onlyEndpoint {
        ulnConfig.setConfigByType(_eid, _oapp, uint32(_configType), _config);
    }

    // ============================ External ===================================

    /// @dev in 301, this is equivalent to execution as in Endpoint V2
    /// @dev dont need to check endpoint deliverable here to save gas, as it will reverts if not deliverable.
    function deliver(bytes calldata _packet, uint _gasLimit) external {
        address receiver = _packet.receiverB20();
        uint16 srcEid = _packet.srcEid().toUint16();

        UlnConfigStruct memory config = ulnConfig.getUlnConfig(receiver, srcEid);
        require(
            // the packet is considered delivered in 301 if all signatures are submitted
            _deliverable(config, _packet.header(), _packet.payloadHash()) == DeliveryState.Delivered,
            Errors.INVALID_ARGUMENT
        );

        _execute(srcEid, _packet.sender(), receiver, _packet.nonce(), _packet.message(), _gasLimit);
    }

    function verify(bytes calldata _packetHeader, bytes32 _payloadHash, uint64 _confirmations) external {
        _verify(_packetHeader, _payloadHash, _confirmations);
    }

    // ============================ View ===================================

    function getConfig(
        uint16 _eid,
        address _oapp,
        uint _configType
    ) external view override returns (bytes memory config) {
        (config, ) = ulnConfig.getConfigByType(_eid, _oapp, uint32(_configType));
    }

    function getDefaultConfig(uint32 _eid, uint32 _configType) external view override returns (bytes memory) {
        return ulnConfig.getDefaultConfigByType(_eid, _configType);
    }

    function version() external pure override returns (uint64 major, uint8 minor, uint8 endpointVersion) {
        return (3, 0, 1);
    }

    /// @dev keeping the same interface as 302
    /// @dev a deliverable message requires it to be endpoint deliverable and ULN deliverable
    function deliverable(bytes calldata _packetHeader, bytes32 _payloadHash) external view returns (DeliveryState) {
        address receiver = _packetHeader.receiverB20();
        uint16 srcEid = _packetHeader.srcEid().toUint16();

        DeliveryState endpointStatus = _endpointDeliverable(srcEid, receiver, _packetHeader);
        if (endpointStatus != DeliveryState.Deliverable) {
            return endpointStatus;
        }

        // check verifier executable
        UlnConfigStruct memory config = ulnConfig.getUlnConfig(receiver, srcEid);
        return _deliverable(config, _packetHeader, _payloadHash);
    }

    // ============================ Internal ===================================

    function _quoteWorkers(
        address _sender,
        uint32 _dstEid,
        WorkerOptions[] memory _options
    ) internal view override returns (uint, address, uint) {
        return _quoteUlnWorkers(_sender, _dstEid, _options);
    }

    function _send(
        Packet memory _packet,
        WorkerOptions[] memory _options
    ) internal virtual override returns (uint, address, uint) {
        return _ulnSend(fees, _options, _packet.sender, _packet.dstEid);
    }

    /// @dev include only the validation logic of ulnv3
    function _deliverable(
        UlnConfigStruct memory _config,
        bytes calldata _packetHeader,
        bytes32 _payloadHash
    ) internal view returns (DeliveryState) {
        // assert packet header is of right size 81
        require(_packetHeader.length == 81, Errors.INVALID_SIZE);
        // assert packet header version is the same as ULN
        require(_packetHeader.version() == packetVersion, Errors.INVALID_VERSION);
        // assert the packet is for this endpoint
        require(_packetHeader.dstEid() == localEid, Errors.INVALID_EID);

        bool signed = verifyConditionMet(_config, keccak256(_packetHeader), _payloadHash);
        if (signed) {
            // in 301, once all signature are gathered, it is ready to be executed
            // marked as Delivered to unify the behavior with 302
            return DeliveryState.Delivered;
        } else {
            return DeliveryState.Signing;
        }
    }

    function _getExecutorOptions(
        bytes memory _options
    ) internal pure override returns (bytes memory, WorkerOptions[] memory) {
        return _getExecutorOptionsInternal(_options);
    }
}
