pragma solidity >=0.8.0;

import "./IMessageLibManager.sol";
import "./IMessagingComposer.sol";
import "./ILayerZeroReceiver.sol";

interface ILayerZeroEndpoint is IMessageLibManager, IMessagingComposer {
    struct MessagingParams {
        uint32 dstEid;
        bytes32 receiver;
        bytes message;
        bytes options;
    }

    struct MessagingReceipt {
        bytes32 guid;
        uint64 nonce;
        MessagingFee fee;
    }

    struct MessagingFee {
        uint nativeFee;
        uint lzTokenFee;
    }

    event PacketSent(bytes encodedPayload, bytes options, address sendLibrary);

    event PacketDelivered(
        uint32 srcEid,
        bytes32 sender,
        address receiver,
        uint64 nonce,
        bytes32 payloadHash,
        address receiveLibrary
    );

    event PacketReceived(
        uint32 srcEid,
        bytes32 sender,
        address receiver,
        uint64 nonce,
        bytes32 payloadHash,
        address caller
    );

    event LzReceiveFailed(
        uint32 srcEid,
        bytes32 sender,
        address receiver,
        uint64 nonce,
        bytes32 payloadHash,
        address caller,
        bytes reason
    );

    event InboundNonceSkipped(uint32 srcEid, bytes32 sender, address receiver, uint64 nonce);

    event FeePaid(address sender, uint nativeOrAltTokenFee, uint lzTokenFee);

    event LayerZeroTokenSet(address token);

    function quote(
        address _sender,
        uint32 _dstEid,
        bytes calldata _message,
        bool _payInLzToken,
        bytes calldata _options
    ) external view returns (MessagingFee memory);

    function send(
        MessagingParams calldata _params,
        uint _lzTokenFee,
        address payable _refundAddress
    ) external payable returns (MessagingReceipt memory);

    function sendWithAlt(
        MessagingParams calldata _params,
        uint _lzTokenFee,
        uint _altTokenFee
    ) external returns (MessagingReceipt memory);

    function deliver(uint32 _srcEid, bytes32 _sender, address _receiver, uint64 _nonce, bytes32 _payloadHash) external;

    function lzReceive(
        address _receiver,
        ILayerZeroReceiver.MessagingContext calldata context,
        bytes32 _guid,
        bytes calldata _callerParams,
        bool _tryCatch
    ) external payable;

    function clear(
        uint32 _srcEid,
        bytes32 _sender,
        uint64 _nonce,
        bytes32 _guid,
        bytes calldata _message
    ) external returns (bytes32 payloadHash);

    function skip(uint32 _srcEid, bytes32 _sender, uint64 _nonce) external;

    function eid() external view returns (uint32);

    function setLayerZeroToken(address _layerZeroToken) external;

    function layerZeroToken() external view returns (address);

    function altFeeToken() external view returns (address);

    function getInboundNonce(address _receiver, uint32 _srcEid, bytes32 _sender) external view returns (uint64);

    function getOutboundNonce(address _sender, uint32 _dstEid, bytes32 _receiver) external view returns (uint64);

    function getInboundPayloadHash(
        address _receiver,
        uint32 _srcEid,
        bytes32 _sender,
        uint64 _nonce
    ) external view returns (bytes32);

    function isSendingMessage() external view returns (bool);

    function getSendContext() external view returns (uint32, address);

    function isReceivingMessage() external view returns (bool);

    function getReceiveContext() external view returns (uint32, address);
}
","keccak256":"0xe8b085dc50842437d42c572eef893642b05b3766cd53c22e61c2471799b028ed","license":"BUSL-1.1"},"@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroReceiver.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.8.0;

interface ILayerZeroReceiver {
    struct MessagingContext {
        uint32 srcEid;
        bytes32 sender;
        bytes message;
        uint64 nonce;
    }

    function nextNonce(uint32 _eid, bytes32 _sender) external view returns (uint64);

    function lzReceive(
        MessagingContext calldata _context,
        bytes32 _guid,
        address _executor,
        bytes calldata _callerParams
    ) external payable;
}
","keccak256":"0x2364fd4a99d178bb5ee34b4fb1e8062f9e0dcd855ed5c72988241695e5eae0ef","license":"BUSL-1.1"},"@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/IMessageLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.8.0;

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "./ILayerZeroEndpoint.sol";
import "./IPacket.sol";

interface IMessageLib is IERC165, IPacket {
    function send(
        Packet calldata _packet,
        bytes calldata _options,
        bool _payInLzToken
    ) external returns (ILayerZeroEndpoint.MessagingReceipt memory, bytes memory encodedPacket, bytes memory options);

    function quote(
        PacketForQuote calldata _packet,
        bool _payInLzToken,
        bytes calldata _options
    ) external view returns (ILayerZeroEndpoint.MessagingFee memory);

    function setTreasury(address _treasury) external;

    function treasury() external view returns (address);

    function setConfig(uint32 _eid, address _oapp, uint32 _configType, bytes calldata _config) external;

    function snapshotConfig(uint32[] calldata _eids, address _oapp) external;

    function resetConfig(uint32[] calldata _eids, address _oapp) external;

    function getConfig(
        uint32 _eid,
        address _oapp,
        uint32 _configType
    ) external view returns (bytes memory config, bool isDefault);

    function getDefaultConfig(uint32 _eid, uint32 _configType) external view returns (bytes memory);

    function withdrawFee(address _to, uint _amount) external;

    function withdrawLzTokenFee(address _to, uint _amount) external;

    // message libs of same major version are compatible
    function version() external view returns (uint64 major, uint8 minor, uint8 endpointVersion);
}
","keccak256":"0xb7c491a347dc7e84af0f1023359109c1f02f048e3019e76dbdb229d77fd234d7","license":"BUSL-1.1"},"@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/IMessageLibManager.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.8.0;

interface IMessageLibManager {
    struct Timeout {
        address lib;
        uint expiry;
    }

    event LibraryRegistered(address newLib);
    event DefaultSendLibrarySet(uint32 eid, address newLib);
    event DefaultReceiveLibrarySet(uint32 eid, address oldLib, address newLib);
    event DefaultReceiveLibraryTimeoutSet(uint32 eid, address oldLib, uint expiry);
    event SendLibrarySet(address sender, uint32 eid, address newLib);
    event ReceiveLibrarySet(address receiver, uint32 eid, address oldLib, address newLib);
    event ReceiveLibraryTimoutSet(address receiver, uint32 eid, address oldLib, uint timeout);

    function registerLibrary(address _lib) external;

    function isRegisteredLibrary(address _lib) external view returns (bool);

    function getRegisteredLibraries() external view returns (address[] memory);

    function setDefaultSendLibrary(uint32 _eid, address _newLib) external;

    function defaultSendLibrary(uint32 _eid) external view returns (address);

    function setDefaultReceiveLibrary(uint32 _eid, address _newLib, uint _timeout) external;

    function defaultReceiveLibrary(uint32 _eid) external view returns (address);

    function setDefaultReceiveLibraryTimeout(uint32 _eid, address _lib, uint _expiry) external;

    function defaultReceiveLibraryTimeout(uint32 _eid) external view returns (address lib, uint expiry);

    function defaultConfig(address _messageLib, uint32 _eid, uint32 _configType) external view returns (bytes memory);

    /// ------------------- OApp interfaces -------------------
    function setSendLibrary(uint32 _eid, address _newLib) external;

    function getSendLibrary(address _sender, uint32 _eid) external view returns (address lib, bool isDefault);

    function setReceiveLibrary(uint32 _eid, address _newLib, uint _gracePeriod) external;

    function getReceiveLibrary(address _receiver, uint32 _eid) external view returns (address lib, bool isDefault);

    function setReceiveLibraryTimeout(uint32 _eid, address _lib, uint _gracePeriod) external;

    function receiveLibraryTimeout(address _receiver, uint32 _eid) external view returns (address lib, uint expiry);

    function setConfig(address _messageLib, uint32 _eid, uint32 _configType, bytes calldata _config) external;

    function getConfig(
        address _oapp,
        address _messageLib,
        uint32 _eid,
        uint32 _configType
    ) external view returns (bytes memory config, bool isDefault);

    function snapshotConfig(address _messageLib, uint32[] calldata _eids) external;

    function resetConfig(address _messageLib, uint32[] calldata _eids) external;
}
","keccak256":"0x349e667ea1885307c0323e84805372ce323bd7b0195aa0a2f9a6f0b41bee968f","license":"BUSL-1.1"},"@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/IMessagingComposer.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.8.0;

interface IMessagingComposer {
    event ComposedMessageDelivered(address receiver, address composer, bytes32 guid, bytes message);
    event ComposedMessageReceived(
        address receiver,
        address composer,
        bytes32 guid,
        bytes32 messageHash,
        address caller
    );
    event LzComposeFailed(
        address sender,
        address receiver,
        bytes32 guid,
        bytes32 messageHash,
        address caller,
        bytes reason
    );

    function deliverComposedMessage(address _composer, bytes32 _guid, bytes calldata _message) external;

    function lzCompose(
        address _receiver,
        address _composer,
        bytes32 _guid,
        bytes calldata _message,
        bytes calldata _callerParams,
        bool _tryCatch
    ) external payable;

    function isComposingMessage() external view returns (bool);

    function getComposeContext() external view returns (address);
}
