interface IPacket {
    struct PacketForQuote {
        address sender;
        uint32 dstEid;
        bytes message;
    }

    struct Packet {
        uint64 nonce;
        uint32 srcEid;
        address sender;
        uint32 dstEid;
        bytes32 receiver;
        bytes message;
    }
}
