pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "hardhat-deploy/solc_0.8/proxy/Proxied.sol";

import "./interfaces/IRelayer.sol";
import "./interfaces/IRelayerFeeLib.sol";
import "./Worker.sol";

contract Relayer is Worker, Proxied, IRelayer {
    // dstEid => DstConfig
    mapping(uint32 => DstConfig) public dstConfig; // for sealing

    function initialize(
        address[] memory _messageLibs,
        address _priceFeed,
        address _roleAdmin,
        address[] memory _admins
    ) public proxied initializer {
        __Worker_init(_messageLibs, _priceFeed, 12000, _roleAdmin, _admins);
    }

    // --- Admin ---
    function setDstConfig(DstConfigParam[] memory _params) external onlyRole(ADMIN_ROLE) {
        for (uint i = 0; i < _params.length; i++) {
            DstConfigParam memory param = _params[i];
            dstConfig[param.dstEid] = DstConfig(
                param.baseGas,
                param.multiplierBps,
                param.floorMarginUSD,
                param.airdropCap
            );
        }
        emit SetDstConfig(_params);
    }

    // --- Message Lib ---
    function assignJob(
        uint32 _dstEid,
        address _sender,
        uint _calldataSize,
        bytes calldata _options
    ) external payable onlyRole(MESSAGE_LIB_ROLE) returns (uint fee) {
        fee = getFee(_dstEid, _sender, _calldataSize, _options);
    }

    // --- Only ACL ---
    function getFee(
        uint32 _dstEid,
        address _sender,
        uint _calldataSize,
        bytes calldata _options
    ) public view onlyAcl(_sender) whenNotPaused returns (uint fee) {
        return
            IRelayerFeeLib(workerLib).getFee(
                priceFeed,
                _dstEid,
                _sender,
                _calldataSize,
                defaultMultiplierBps,
                dstConfig[_dstEid],
                _options
            );
    }

    // todo: prevent airdrop double spend?

    // --- Internal ---
    function _checkWorkerInterface(address _worker) internal view override {
        require(
            IERC165(_worker).supportsInterface(type(IRelayerFeeLib).interfaceId),
            "Relayer: worker does not implement IRelayerFeeLib"
        );
    }
}
","keccak256":"0xafa71851a174445201c656dd179a61dcf61b5cf4c306bab27d0c6b3571343758","license":"BUSL-1.1"},"contracts/Worker.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/IMessageLib.sol";
import "./interfaces/ILayerZeroPriceFeed.sol";
import "./interfaces/IWorker.sol";

abstract contract Worker is Initializable, AccessControlUpgradeable, IWorker {
    bytes32 public constant MESSAGE_LIB_ROLE = keccak256("MESSAGE_LIB_ROLE");
    bytes32 public constant ALLOWLIST = keccak256("ALLOWLIST");
    bytes32 public constant DENYLIST = keccak256("DENYLIST");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    address public workerLib;

    uint64 public allowlistSize;
    uint16 public defaultMultiplierBps;
    ILayerZeroPriceFeed public priceFeed;

    bool public paused;

    function __Worker_init(
        address[] memory _messageLibs,
        address _priceFeed,
        uint16 _defaultMultiplierBps,
        address _roleAdmin,
        address[] memory _admins
    ) internal onlyInitializing {
        __Worker_init_unchained(_messageLibs, _priceFeed, _defaultMultiplierBps, _roleAdmin, _admins);
    }

    function __Worker_init_unchained(
        address[] memory _messageLibs,
        address _priceFeed,
        uint16 _defaultMultiplierBps,
        address _roleAdmin,
        address[] memory _admins
    ) internal onlyInitializing {
        defaultMultiplierBps = _defaultMultiplierBps;

        priceFeed = ILayerZeroPriceFeed(_priceFeed);

        _grantRole(DEFAULT_ADMIN_ROLE, _roleAdmin); // _roleAdmin can grant and revoke all roles

        for (uint i = 0; i < _messageLibs.length; i++) {
            _grantRole(MESSAGE_LIB_ROLE, _messageLibs[i]);
        }

        for (uint i = 0; i < _admins.length; i++) {
            _grantRole(ADMIN_ROLE, _admins[i]);
        }
    }

    /// 1) If one address is in the deny list -> deny
    /// 2) else If the allow list is empty (allows everyone) || address in the allow list -> allow
    /// 3) deny otherwise
    modifier onlyAcl(address _sender) {
        if (hasRole(DENYLIST, _sender)) {
            revert("Worker: not allowed");
        } else if (allowlistSize == 0 || hasRole(ALLOWLIST, _sender)) {
            _;
        } else {
            revert("Worker: not allowed");
        }
    }

    modifier whenNotPaused() {
        require(!paused, "Worker: paused");
        _;
    }

    // --- RoleAdmin ---
    function setPaused(bool _paused) external onlyRole(DEFAULT_ADMIN_ROLE) {
        paused = _paused;
    }

    // -- Admin --
    function setPriceFeed(address _priceFeed) external onlyRole(ADMIN_ROLE) {
        priceFeed = ILayerZeroPriceFeed(_priceFeed);
        emit SetPriceFeed(_priceFeed);
    }

    function setWorkerLib(address _workerLib) external onlyRole(ADMIN_ROLE) {
        _checkWorkerInterface(_workerLib);
        workerLib = _workerLib;
        emit SetWorkerLib(_workerLib);
    }

    function setDefaultMultiplierBps(uint16 _multiplierBps) external onlyRole(ADMIN_ROLE) {
        defaultMultiplierBps = _multiplierBps;
        emit SetDefaultMultiplierBps(_multiplierBps);
    }

    function withdrawFee(address _lib, address payable _to, uint _amount) external virtual onlyRole(ADMIN_ROLE) {
        require(hasRole(MESSAGE_LIB_ROLE, _lib), "Worker: Invalid message lib");
        IMessageLib(_lib).withdrawFee(_to, _amount);
        emit Withdraw(_lib, _to, _amount);
    }

    function _checkWorkerInterface(address _workerLib) internal view virtual;

    // -- Override ACL --
    function _grantRole(bytes32 role, address account) internal override {
        if (role == ALLOWLIST && !hasRole(role, account)) {
            allowlistSize++;
        }
        super._grantRole(role, account);
    }

    function _revokeRole(bytes32 role, address account) internal override {
        if (role == ALLOWLIST && hasRole(role, account)) {
            allowlistSize--;
        }
        super._revokeRole(role, account);
    }

    function renounceRole(bytes32 /*role*/, address /*account*/) public virtual override {
        revert("Worker: cannot renounce role");
    }

    function getPrice(uint32 _dstEid) public view returns (uint128, uint64) {
        ILayerZeroPriceFeed.Price memory price = priceFeed.getPrice(_dstEid);
        return (price.priceRatio, price.gasPriceInUnit);
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[49] private __gap;
}
