pragma solidity >=0.8.0;

interface ILayerZeroRelayer {
    // @notice query price and assign jobs at the same time
    // @param _dstEid - the destination endpoint identifier
    // @param _sender - the source sending contract address. relayers may apply price discrimination to senders
    // @param _calldataSize - dynamic data size of message + caller params
    // @param _options - optional parameters for extra service plugins, e.g. sending dust tokens at the destination chain
    function assignJob(
        uint32 _dstEid,
        address _sender,
        uint _calldataSize,
        bytes calldata _options
    ) external payable returns (uint price);

    // @notice query the relayer price for relaying the payload and its proof to the destination chain
    // @param _dstEid - the destination endpoint identifier
