pragma solidity ^0.8.0;

import "solidity-bytes-utils/contracts/BytesLib.sol";

library Options {
    using BytesLib for bytes;

    struct WorkerOptions {
        uint16 workerId;
        bytes options;
    }

    struct Option {
        uint16 optionType;
        bytes params;
    }

    uint16 public constant RELAYER_WORKER_ID = 1; // matches RelayerOptions WORKER_ID

    uint16 public constant TYPE_1 = 1; // simple option type 1
    uint16 public constant TYPE_2 = 2; // simple option type 2
    uint16 public constant TYPE_3 = 3; // cannot overlap with simple option types

    /// ======================= Decoding Functions =======================
    function decodeOptions(bytes memory _options) internal pure returns (WorkerOptions[] memory) {
        uint16 formatType = _options.toUint16(0);
        if (formatType == TYPE_1 || formatType == TYPE_2) {
            WorkerOptions[] memory options = _convert(formatType, _options);
