pragma solidity >=0.8.0;

import "../../interfaces/IPacket.sol";

library PacketV1Codec {
    // header (version + nonce + path)
    // version
    uint private constant PACKET_VERSION_OFFSET = 0;
    //    nonce
    uint private constant NONCE_OFFSET = 1;
    //    path
    uint private constant SRC_EID_OFFSET = 9;
    uint private constant SRC_ADDRESS_OFFSET = 13;
    uint private constant DST_EID_OFFSET = 45;
    uint private constant DST_ADDRESS_OFFSET = 49;
    // payload (guid + message)
    uint private constant GUID_OFFSET = 81; // keccak256(nonce + path)
    uint private constant MESSAGE_OFFSET = 113;

    function encode(
        uint8 _version,
        IPacket.Packet calldata _packet
    ) internal pure returns (bytes32 id, bytes memory encodedPacket) {
        bytes memory nonceAndPath = abi.encodePacked(_packet.nonce, encodePath(_packet));
        id = keccak256(nonceAndPath);
        encodedPacket = abi.encodePacked(_version, nonceAndPath, id, _packet.message);
    }

    function encodePath(IPacket.Packet calldata _packet) internal pure returns (bytes memory path) {
        path = abi.encodePacked(_packet.srcEid, addressToBytes32(_packet.sender), _packet.dstEid, _packet.receiver);
    }

    function header(bytes calldata _packet) internal pure returns (bytes memory) {
        return _packet[0:GUID_OFFSET];
    }

    function version(bytes calldata _packet) internal pure returns (uint8) {
        return uint8(bytes1(_packet[PACKET_VERSION_OFFSET:NONCE_OFFSET]));
    }

    function nonce(bytes calldata _packet) internal pure returns (uint64) {
        return uint64(bytes8(_packet[NONCE_OFFSET:SRC_EID_OFFSET]));
    }

    function srcEid(bytes calldata _packet) internal pure returns (uint32) {
        return uint32(bytes4(_packet[SRC_EID_OFFSET:SRC_ADDRESS_OFFSET]));
    }

    function sender(bytes calldata _packet) internal pure returns (bytes32) {
        return bytes32(_packet[SRC_ADDRESS_OFFSET:DST_EID_OFFSET]);
    }

    function senderAddressB20(bytes calldata _packet) internal pure returns (address) {
        return bytes32ToAddress(sender(_packet));
    }

    function dstEid(bytes calldata _packet) internal pure returns (uint32) {
        return uint32(bytes4(_packet[DST_EID_OFFSET:DST_ADDRESS_OFFSET]));
    }

    function receiver(bytes calldata _packet) internal pure returns (bytes32) {
        return bytes32(_packet[DST_ADDRESS_OFFSET:GUID_OFFSET]);
    }

    function receiverB20(bytes calldata _packet) internal pure returns (address) {
        return bytes32ToAddress(receiver(_packet));
    }

    function guid(bytes calldata _packet) internal pure returns (bytes32) {
        return bytes32(_packet[GUID_OFFSET:MESSAGE_OFFSET]);
    }

    function message(bytes calldata _packet) internal pure returns (bytes calldata) {
        return bytes(_packet[MESSAGE_OFFSET:]);
    }

    function payload(bytes calldata _packet) internal pure returns (bytes calldata) {
        return bytes(_packet[GUID_OFFSET:]);
    }

    function addressToBytes32(address _addr) internal pure returns (bytes32) {
        return bytes32(uint(uint160(_addr)));
    }

    function bytes32ToAddress(bytes32 _b) internal pure returns (address) {
        return address(uint160(uint(_b)));
    }
}
","keccak256":"0xccaf35d405c41ab519c8aa48e352c993ee6e7eda7e7d4f80a5fb9abfa8c39b33","license":"BUSL-1.1"},"@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/RelayerOptions.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "solidity-bytes-utils/contracts/BytesLib.sol";
import "./Options.sol";

library RelayerOptions {
    using BytesLib for bytes;

    uint16 public constant WORKER_ID = 1;

    uint16 public constant OPTION_TYPE_LZRECEIVE = 1;
    uint16 public constant OPTION_TYPE_AIRDROP = 2;
    uint16 public constant OPTION_TYPE_LZCOMPOSE = 3;

    function decodeLzReceiveParams(bytes memory _params) internal pure returns (uint gas, uint value) {
        require(_params.length == 32 || _params.length == 64, "LZ10009");
        gas = _params.toUint256(0);
        value = _params.length == 64 ? _params.toUint256(32) : 0;
    }

    function decodeAirdropParams(bytes memory _params) internal pure returns (uint amount, bytes32 receiver) {
        require(_params.length == 64, "LZ10009");
        amount = _params.toUint256(0);
        receiver = _params.toBytes32(32);
    }

    function decodeLzComposeParams(bytes memory _params) internal pure returns (uint gas, uint value) {
        require(_params.length == 64, "LZ10009");
        gas = _params.toUint256(0);
        value = _params.toUint256(32);
    }

    function encodeLzReceiveOption(uint _gas, uint _value) internal pure returns (Options.Option memory) {
        bytes memory params = _value == 0 ? abi.encodePacked(_gas) : abi.encodePacked(_gas, _value);
        return Options.Option(OPTION_TYPE_LZRECEIVE, params);
    }

    function encodeAirdropOption(uint _amount, bytes32 _receiver) internal pure returns (Options.Option memory) {
        return Options.Option(OPTION_TYPE_AIRDROP, abi.encodePacked(_amount, _receiver));
    }

    function encodeLzComposeOption(uint _gas, uint _value) internal pure returns (Options.Option memory) {
        return Options.Option(OPTION_TYPE_LZCOMPOSE, abi.encodePacked(_gas, _value));
    }
}
