","keccak256":"0xde43910f8b1439d1d5845d5d563c2209d41e690f64ffdaf792c0eee43f710519","license":"BUSL-1.1"},"contracts/uln/UlnConfig.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract UlnConfig is Ownable {
    struct Config {
        uint64 inboundConfirmations;
        bool useCustomOracles; // unused for default config
        bool useCustomOptionalOracles; // unused for default config
        uint16 oraclesCount;
        uint16 optionalOraclesCount;
        uint16 optionalOraclesThreshold;
        address[] oracles; // allowed overlap with optionalOracles
        address[] optionalOracles; // allowed overlap with oracles
    }

    event SetDefaultConfig(
        uint32 indexed eid,
        uint64 inboundBlockConfirm,
        address[] oracles,
        address[] optionalOracles,
        uint16 optionalOraclesThreshold
    );
    event UlnConfigUpdated(address indexed oapp, uint indexed configType, bytes newConfig);

    // Application config, extending from MessageLibBase
    uint32 public constant CONFIG_TYPE_INBOUND_CONFIRMATIONS = 4;
    uint32 public constant CONFIG_TYPE_ORACLES = 5;
    uint32 public constant CONFIG_TYPE_OPTIONAL_ORACLES = 6;

    // [oapp][eid] => UlnConfig
    mapping(address => mapping(uint32 => Config)) public ulnConfig;
    // [eid] => UlnConfig
    mapping(uint32 => Config) public defaultUlnConfig;

    function getUlnConfig(address _oapp, uint32 _remoteEid) public view returns (Config memory) {
        Config memory config = ulnConfig[_oapp][_remoteEid];
        Config storage defaultConfig = defaultUlnConfig[_remoteEid];
        require(defaultConfig.inboundConfirmations > 0, "LZ10008");

        if (config.inboundConfirmations == 0) {
            config.inboundConfirmations = defaultConfig.inboundConfirmations;
        }

        if (!config.useCustomOracles) {
            config.oracles = defaultConfig.oracles;
            config.oraclesCount = defaultConfig.oraclesCount;
        }

        if (!config.useCustomOptionalOracles) {
            config.optionalOracles = defaultConfig.optionalOracles;
            config.optionalOraclesCount = defaultConfig.optionalOraclesCount;
            config.optionalOraclesThreshold = defaultConfig.optionalOraclesThreshold;
        }

        require(config.oraclesCount > 0 || config.optionalOraclesThreshold > 0, "LZD0005");

        return config;
    }

    function _setUlnConfig(uint32 _remoteEid, address _oapp, uint32 _configType, bytes calldata _config) internal {
        Config storage config = ulnConfig[_oapp][_remoteEid];
        if (_configType == CONFIG_TYPE_INBOUND_CONFIRMATIONS) {
            uint64 blockConfirmations = abi.decode(_config, (uint64));
            config.inboundConfirmations = blockConfirmations;
        } else if (_configType == CONFIG_TYPE_ORACLES) {
            // oracles list must be sorted by ascending order and contain no duplicates
            (bool useCustomOracles, address[] memory oracles) = abi.decode(_config, (bool, address[]));

            uint oracleCount = oracles.length;
            bool isValidCount = useCustomOracles ? oracleCount <= 65535 : oracleCount == 0;
            require(isValidCount, "LZ10009");
            _assertNoDuplicates(oracles, oracleCount);

            config.oraclesCount = uint16(oracleCount);
            config.oracles = oracles;
            config.useCustomOracles = useCustomOracles;

            getUlnConfig(_oapp, _remoteEid); // validate the latest config by getting it
        } else if (_configType == CONFIG_TYPE_OPTIONAL_ORACLES) {
            // oracles list must be sorted by ascending order and contain no duplicates
            (bool useCustomOptionalOracles, address[] memory optionalOracles, uint16 threshold) = abi.decode(
                _config,
                (bool, address[], uint16)
            );

            uint optionalOraclesCount = optionalOracles.length;
            bool isValidCount = useCustomOptionalOracles ? optionalOraclesCount <= 65535 : optionalOraclesCount == 0;
            require(isValidCount, "LZ10009");
            require(threshold <= optionalOraclesCount, "LZ10003");
            _assertNoDuplicates(optionalOracles, optionalOraclesCount);

            config.optionalOraclesCount = uint16(optionalOraclesCount);
            config.optionalOracles = optionalOracles;
            config.optionalOraclesThreshold = threshold;
            config.useCustomOptionalOracles = useCustomOptionalOracles;

            getUlnConfig(_oapp, _remoteEid); // validate the latest config by getting it
        } else {
            revert("LZC0000");
        }

        emit UlnConfigUpdated(_oapp, _configType, _config);
    }

    function _getUlnConfig(
        uint32 _remoteEid,
        address _oapp,
        uint _configType
    ) internal view returns (bytes memory, bool) {
        Config storage config = ulnConfig[_oapp][_remoteEid];
        Config storage defaultConfig = defaultUlnConfig[_remoteEid];

        if (_configType == CONFIG_TYPE_INBOUND_CONFIRMATIONS) {
            if (config.inboundConfirmations == 0) {
                return (abi.encode(defaultConfig.inboundConfirmations), true);
            }
            return (abi.encode(config.inboundConfirmations), false);
        } else if (_configType == CONFIG_TYPE_ORACLES) {
            if (config.useCustomOracles) {
                return (abi.encode(config.oracles), false);
            }
            return (abi.encode(defaultConfig.oracles), true);
        } else if (_configType == CONFIG_TYPE_OPTIONAL_ORACLES) {
            if (config.useCustomOptionalOracles) {
                return (abi.encode(config.optionalOracles, config.optionalOraclesThreshold), false);
            }
            return (abi.encode(defaultConfig.optionalOracles, defaultConfig.optionalOraclesThreshold), true);
        } else {
            revert("LZC0000");
        }
    }

    function _getDefaultUlnConfig(uint32 _remoteEid, uint _configType) internal view returns (bytes memory) {
        Config storage config = defaultUlnConfig[_remoteEid];

        if (_configType == CONFIG_TYPE_INBOUND_CONFIRMATIONS) {
            return abi.encode(config.inboundConfirmations);
        } else if (_configType == CONFIG_TYPE_ORACLES) {
            return abi.encode(config.oracles);
        } else if (_configType == CONFIG_TYPE_OPTIONAL_ORACLES) {
            return abi.encode(config.optionalOracles, config.optionalOraclesThreshold);
        } else {
            revert("LZC0000");
        }
    }

    function _snapshotUlnConfig(uint32[] calldata _eids, address _oapp) internal {
        for (uint i = 0; i < _eids.length; i++) {
            uint32 eid = _eids[i];
            Config memory config = getUlnConfig(_oapp, eid);
            ulnConfig[_oapp][eid] = config;
        }
    }

    function _resetUlnConfig(uint32[] calldata _eids, address _oapp) internal {
        for (uint i = 0; i < _eids.length; i++) {
            uint32 eid = _eids[i];
            delete ulnConfig[_oapp][eid];
        }
    }

    function setDefaultConfig(
        uint32 _eid,
        uint64 _inboundConfirmations,
        address[] calldata _oracles,
        address[] calldata _optionalOracles,
        uint16 _optionalOraclesThreshold
    ) external onlyOwner {
        uint oracleCount = _oracles.length;
        uint optionalOracleCount = _optionalOracles.length;

        require(_inboundConfirmations > 0, "LZ10000");
        require(
            oracleCount <= 65535 && optionalOracleCount <= 65535 && _optionalOraclesThreshold <= optionalOracleCount,
            "LZ10009"
        );
        require(oracleCount > 0 || _optionalOraclesThreshold > 0, "LZ10011");
        _assertNoDuplicates(_oracles, oracleCount);
        _assertNoDuplicates(_optionalOracles, optionalOracleCount);

        defaultUlnConfig[_eid] = Config(
            _inboundConfirmations,
            false, // unused for default config
            false, // unused for default config
            uint16(oracleCount),
            uint16(optionalOracleCount),
            _optionalOraclesThreshold,
            _oracles,
            _optionalOracles
        );

        emit SetDefaultConfig(_eid, _inboundConfirmations, _oracles, _optionalOracles, _optionalOraclesThreshold);
    }

    function _assertNoDuplicates(address[] memory _oracles, uint _oracleCount) internal pure {
        address lastOracle = address(0);
        for (uint i = 0; i < _oracleCount; i++) {
            address oracle = _oracles[i];
            require(oracle > lastOracle, "LZ10006"); // to ensure no duplicates
            lastOracle = oracle;
        }
    }
}
","keccak256":"0xf0f1d3390760e25f96503f573e6cb6c242088d0cee3affbcc7c042a2f196ece2","license":"BUSL-1.1"},"contracts/uln/UltraLightNode302.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@layerzerolabs/lz-evm-protocol-v2/contracts/messagelib/libs/PacketV1Codec.sol";

import "./interfaces/ILayerZeroOracle.sol";

import "./UlnConfig.sol";
import "../MessageLibBase.sol";
import "../libs/OracleOptions.sol";

contract UltraLightNode302 is MessageLibBase, UlnConfig {
    using PacketV1Codec for bytes;
    // header hash => message hash => confirmations
    mapping(bytes32 => mapping(bytes32 => mapping(address => uint64))) public hashLookup;

    event PayloadSigned(address oracle, bytes header, uint confirmations, bytes32 proofHash);
    event OracleFee(address[] oracles, uint[] fees);

    constructor(address _endpoint, uint _baseGasEstimate) MessageLibBase(_endpoint, 1, _baseGasEstimate) {}

    function _quoteWorkers(
        address _oapp,
        uint32 _dstEid,
        Options.WorkerOptions[] memory _options,
        uint64 _outboundConfirmations
    ) internal view override returns (uint totalFee) {
        Config memory config = getUlnConfig(_oapp, _dstEid);

        (, , bytes memory oracleOptions) = Options.getWorkerOptionsByWorkerId(_options, OracleOptions.WORKER_ID);
        // quote the oracles
        totalFee += _quoteOracles(
            config.oracles,
            config.oraclesCount,
            _oapp,
            _dstEid,
            oracleOptions,
            _outboundConfirmations
        );
        // quote the optional oracles
        totalFee += _quoteOracles(
            config.optionalOracles,
            config.optionalOraclesCount,
            _oapp,
            _dstEid,
            oracleOptions,
            _outboundConfirmations
        );
    }

    function _quoteOracles(
        address[] memory _oracles,
        uint16 _oracleCount,
        address _oapp,
        uint32 _dstEid,
        bytes memory _options,
        uint64 _outboundConfirmations
    ) internal view returns (uint256 totalFee) {
        for (uint i = 0; i < _oracleCount; i++) {
            totalFee += ILayerZeroOracle(_oracles[i]).getFee(_dstEid, _outboundConfirmations, _oapp, _options);
        }
    }

    function _send(
        Packet calldata _packet,
        Options.WorkerOptions[] memory _options,
        uint64 _outboundConfirmations
    ) internal virtual override returns (uint totalFee) {
        // assert payload size restriction
        address oapp = _packet.sender;
        Config memory config = getUlnConfig(oapp, _packet.dstEid);

        // oracle fees
        (, , bytes memory oracleOptions) = Options.getWorkerOptionsByWorkerId(_options, OracleOptions.WORKER_ID);
        totalFee += _payOracles(
            config.oracles,
            config.oraclesCount,
            oapp,
            _packet.dstEid,
            oracleOptions,
            _outboundConfirmations
        );
        totalFee += _payOracles(
            config.optionalOracles,
            config.optionalOraclesCount,
            oapp,
            _packet.dstEid,
            oracleOptions,
            _outboundConfirmations
        );
    }

    function _payOracles(
        address[] memory _oracles,
        uint16 _oracleCount,
        address _oapp,
        uint32 _dstEid,
        bytes memory _options,
        uint64 _outboundConfirmations
    ) internal returns (uint totalFee) {
        uint[] memory oracleFees = new uint[](_oracleCount);
        for (uint i = 0; i < _oracleCount; i++) {
            uint oracleFee = ILayerZeroOracle(_oracles[i]).assignJob(_dstEid, _outboundConfirmations, _oapp, _options);
            oracleFees[i] = oracleFee;

            if (oracleFee > 0) {
                fees[_oracles[i]] += oracleFee;
                totalFee += oracleFee;
            }
        }

        emit OracleFee(_oracles, oracleFees);
    }

    function _snapshotConfig(uint32[] calldata _eids, address _oapp) internal override {
        _snapshotUlnConfig(_eids, _oapp);
    }

    function _setConfig(uint32 _eid, address _oapp, uint32 _configType, bytes calldata _config) internal override {
        _setUlnConfig(_eid, _oapp, _configType, _config);
    }

    function _resetConfig(uint32[] calldata _eids, address _oapp) internal override {
        _resetUlnConfig(_eids, _oapp);
    }

    function _getConfig(
        uint32 _eid,
        address _oapp,
        uint32 _configType
    ) internal view override returns (bytes memory, bool) {
        return _getUlnConfig(_eid, _oapp, _configType);
    }

    function _defaultConfig(uint32 _eid, uint32 _configType) internal view override returns (bytes memory) {
        return _getDefaultUlnConfig(_eid, _configType);
    }

    function deliver(bytes calldata _packetHeader, bytes32 _payloadHash) external {
        Config memory config = getUlnConfig(_packetHeader.receiverB20(), _packetHeader.srcEid());
        require(_deliverable(config, _packetHeader, _payloadHash), "LZ10000");

        ILayerZeroEndpoint(endpoint).deliver(
            _packetHeader.srcEid(),
            _packetHeader.sender(),
            _packetHeader.receiverB20(),
            _packetHeader.nonce(),
            _payloadHash
        );
    }

    function oracleSign(bytes calldata _packetHeader, bytes32 _payloadHash, uint64 _confirmations) external {
        hashLookup[keccak256(_packetHeader)][_payloadHash][msg.sender] = _confirmations;
        emit PayloadSigned(msg.sender, _packetHeader, _confirmations, _payloadHash);
    }

    function version() external pure override returns (uint64 major, uint8 minor, uint8 endpointVersion) {
        return (3, 0, 2);
    }

    function deliverable(bytes calldata _packetHeader, bytes32 _payloadHash) external view returns (bool) {
        Config memory config = getUlnConfig(_packetHeader.receiverB20(), _packetHeader.srcEid());
        return _deliverable(config, _packetHeader, _payloadHash);
    }

    function _deliverable(
        Config memory _config,
        bytes calldata _packetHeader,
        bytes32 _payloadHash
    ) internal view returns (bool) {
        require(_packetHeader.length == 81, "LZ10009");
        require(_packetHeader.version() == packetVersion, "LZ10007");
        require(_packetHeader.dstEid() == localEid, "LZ10008");

        bytes32 _headerHash = keccak256(_packetHeader);
        uint64 requiredConfirmations = _config.inboundConfirmations;
        // iterate the must-have oracles
        for (uint i = 0; i < _config.oraclesCount; i++) {
            address oracle = _config.oracles[i];
            uint64 confirmations = hashLookup[_headerHash][_payloadHash][oracle];
            if (confirmations < requiredConfirmations) {
                // return false if any of the must-have oracles haven't signed
                return false;
            }
        }

        if (_config.oraclesCount > 0 && _config.optionalOraclesCount == 0) {
            return true;
        } else {
            // then the optional ones
            uint optionalCount = 0;
            for (uint i = 0; i < _config.optionalOraclesCount; i++) {
                address oracle = _config.optionalOracles[i];
                uint64 confirmations = hashLookup[_headerHash][_payloadHash][oracle];

                if (confirmations >= requiredConfirmations) {
                    optionalCount++;
                    if (optionalCount >= _config.optionalOraclesThreshold) {
                        return true;
                    }
                }
            }
        }

        // return false by default as a catch-all
        return false;
    }
}
