pragma solidity 0.8.17;

import "./PreCrimeBase.sol";

abstract contract PreCrime is PreCrimeBase {
    error SimulateRevert(uint16 code, bytes message);

    constructor(address _endpoint) PreCrimeBase(_endpoint) {}

    function simulate(Packet[] calldata _packets) external payable override returns (uint16 code, bytes memory data) {
        require(tx.origin == msg.sender, "only EOA can call simulate");

        (code, data) = _checkPacketsMaxSizeAndNonceOrder(_packets);
        if (code != CODE_SUCCESS) {
            return (code, data);
        }

        (bool success, bytes memory result) = address(this).call{value: msg.value}(
            abi.encodeWithSelector(this._simulateAndRevert.selector, _packets)
        );
        // refund for those who send eth to simulate accidentally
        if (msg.value > 0) {
            payable(msg.sender).transfer(msg.value);
        }
        // the _simulateAndRevert will never return success
        require(!success, "simulate should never return success");

        (code, data) = _parseRevertResult(result);
        if (code == CODE_SUCCESS) {
            data = abi.encode(localEid, data); // add localEid to the header
        }
    }

    function simulateView(
        Packet[] calldata /*_packets*/
    ) external pure override returns (uint16 /*code*/, bytes memory /*result*/) {
        revert("not implemented");
    }

    function _parseRevertResult(bytes memory result) internal pure returns (uint16 code, bytes memory data) {
        if (bytes4(result) != SimulateRevert.selector) {
            assembly {
                revert(add(result, 0x20), mload(result))
            }
        }
        // parse code and result
        assembly {
            // Slice the sighash. Remove the selector which is the first 4 bytes
            result := add(result, 0x04)
        }
        return abi.decode(result, (uint16, bytes));
    }

    function _simulateAndRevert(Packet[] calldata _packets) external payable {
        require(msg.sender == address(this), "Precrime: only self");
        (uint16 code, bytes memory result) = _simulate(_packets);
        revert SimulateRevert(code, result);
    }

    // ----------------- to be implemented -----------------
    function _simulate(Packet[] calldata _packets) internal virtual returns (uint16 code, bytes memory result);
}
","keccak256":"0xf3191dc2fd61ee5e5fdca9468a9705ea987b087685d14e4ba1f187386ca1f9fd","license":"MIT"},"contracts/PreCrimeBase.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroEndpoint.sol";
import "./interfaces/IPreCrime.sol";

abstract contract PreCrimeBase is IPreCrime {
    uint16 public constant PRECRIME_VERSION = 1;
    uint16 public constant CONFIG_VERSION = 1;

    //---------------- error code ----------------------
    // --- UA scope code ---
    uint16 public constant CODE_SUCCESS = 0; // success
    uint16 public constant CODE_PRECRIME_FAILURE = 1; // !!! crimes found

    // --- protocol scope error code ---
    // simualte
    uint16 public constant CODE_PACKETS_OVERSIZE = 400; // packets number bigger then max size
    uint16 public constant CODE_PACKETS_UNSORTED = 401; // packets are unsorted, need backfill and keep order
    // precrime
    uint16 public constant CODE_MISS_SIMULATE_RESULT = 402; // miss simulation result

    ILayerZeroEndpoint public immutable lzEndpoint;
    uint32 public localEid;

    address public pAdmin;

    uint64 public pMaxBatchSize;
    uint32[] public precrimeEids;
    bytes32[] public precrimePeers;
    mapping(uint32 => bytes32) public precrimeEidToPeer;

    modifier onlyPrecrimeAdmin() {
        require(msg.sender == pAdmin, "only admin");
        _;
    }

    constructor(address _endpoint) {
        lzEndpoint = ILayerZeroEndpoint(_endpoint);
        localEid = lzEndpoint.eid();
        pAdmin = msg.sender;
    }

    // ----------------- admin functions -----------------
    function setPrecrimeAdmin(address _admin) external onlyPrecrimeAdmin {
        pAdmin = _admin;
    }

    function setPrecrimeMaxBatchSize(uint64 _maxBatchSize) external onlyPrecrimeAdmin {
        pMaxBatchSize = _maxBatchSize;
    }

    function setPrecrimePeers(uint32[] memory _eids, bytes32[] memory _peers) external onlyPrecrimeAdmin {
        precrimeEids = _eids;
        precrimePeers = _peers;
        for (uint i = 0; i < _eids.length; i++) {
            precrimeEidToPeer[_eids[i]] = _peers[i];
        }
    }

    // ----------------- view functions -----------------

    function getPrecrimeConfig(Packet[] calldata _packets) external view virtual override returns (bytes memory) {
        (uint32[] memory eids, bytes32[] memory peers) = _getPrecrimePeers(_packets);
        return
            abi.encodePacked(
                CONFIG_VERSION,
                //---- max packets size for simulate batch ---
                pMaxBatchSize,
                //------------- peer precrimes -------------
                eids.length,
                eids,
                peers
            );
    }

    function precrime(
        Packet[] calldata _packets,
        bytes[] calldata _simulation
    ) external view override returns (uint16 code, bytes memory reason) {
        bytes[] memory originSimulateResult = new bytes[](_simulation.length);
        uint32[] memory eids = new uint32[](_simulation.length);
        for (uint256 i = 0; i < _simulation.length; i++) {
            (uint32 eid, bytes memory simulateResult) = abi.decode(_simulation[i], (uint32, bytes));
            eids[i] = eid;
            originSimulateResult[i] = simulateResult;
        }

        (code, reason) = _checkResultsCompleteness(_packets, eids);
        if (code != CODE_SUCCESS) {
            return (code, reason);
        }

        (code, reason) = _precrime(originSimulateResult);
    }

    function precrimeVersion() external pure override returns (uint16) {
        return PRECRIME_VERSION;
    }

    function _checkResultsCompleteness(
        Packet[] calldata _packets,
        uint32[] memory _resultEids
    ) internal view returns (uint16 code, bytes memory reason) {
        // check if all peers result included
        if (_packets.length > 0) {
            (uint32[] memory eids, ) = _getPrecrimePeers(_packets);
            for (uint256 i = 0; i < eids.length; i++) {
                bool resultEidChecked;
                for (uint256 j = 0; j < _resultEids.length; j++) {
                    if (_resultEids[j] == eids[i]) {
                        resultEidChecked = true;
                        break;
                    }
                }
                if (!resultEidChecked) {
                    return (CODE_MISS_SIMULATE_RESULT, "missing peer simulation result");
                }
            }
        }
        // check if local resut included
        bool localEidResultChecked;
        for (uint256 j = 0; j < _resultEids.length; j++) {
            if (_resultEids[j] == localEid) {
                localEidResultChecked = true;
                break;
            }
        }
        if (!localEidResultChecked) {
            return (CODE_MISS_SIMULATE_RESULT, "missing local simulation result");
        }

        return (CODE_SUCCESS, "");
    }

    function _checkPacketsMaxSizeAndNonceOrder(
        Packet[] calldata _packets
    ) internal view returns (uint16 code, bytes memory reason) {
        if (_packets.length > pMaxBatchSize) {
            return (
                CODE_PACKETS_OVERSIZE,
                abi.encodePacked("packets size exceed limited ", Strings.toString(uint256(pMaxBatchSize)))
            );
        }

        // check packets nonce, sequence order
        // packets should group by srcEid and sender, then sort by nonce ascending
        if (_packets.length > 0) {
            uint32 srcEid;
            bytes32 sender;
            uint64 nonce;
            for (uint256 i = 0; i < _packets.length; i++) {
                Packet memory packet = _packets[i];
                // start from a new chain packet or a new source UA
                if (packet.context.srcEid != srcEid || packet.context.sender != sender) {
                    srcEid = packet.context.srcEid;
                    sender = packet.context.sender;
                    nonce = packet.context.nonce;
                    uint64 nextInboundNonce = lzEndpoint.getInboundNonce(_receiver(), srcEid, sender) + 1;
                    // the first packet's nonce must equal to dst InboundNonce+1
                    if (nonce != nextInboundNonce) {
                        return (
                            CODE_PACKETS_UNSORTED,
                            abi.encodePacked("skipped inboundNonce, packet index: ", Strings.toString(i))
                        );
                    }
                } else {
                    // the following packet's nonce add 1 in order
                    if (packet.context.nonce != ++nonce) {
                        return (
                            CODE_PACKETS_UNSORTED,
                            abi.encodePacked("unsorted packets, packet index: ", Strings.toString(i))
                        );
                    }
                }
            }
        }
        return (CODE_SUCCESS, "");
    }

    function _getPrecrimePeers(Packet[] calldata _packets) internal view returns (uint32[] memory, bytes32[] memory) {
        if (_packets.length == 0) {
            return (precrimeEids, precrimePeers);
        }

        // only return related peers
        return _filterByPackets(_packets);
    }

    function _filterByPackets(
        Packet[] calldata _packets
    ) internal view returns (uint32[] memory eids, bytes32[] memory peers) {
        uint16 size;
        for (uint i = 0; i < _packets.length; i++) {
            if (precrimeEidToPeer[_packets[i].context.srcEid] != bytes32(0x0)) {
                size++;
            }
        }

        eids = new uint32[](size);
        peers = new bytes32[](size);
        for (uint i = 0; i < _packets.length; i++) {
            bytes32 peer = precrimeEidToPeer[_packets[i].context.srcEid];
            if (peer != bytes32(0x0)) {
                eids[i] = _packets[i].context.srcEid;
                peers[i] = peer;
            }
        }
        return (eids, peers);
    }

    // ----------------- to be implemented -----------------
    function _receiver() internal view virtual returns (address);

    function _precrime(bytes[] memory _simulation) internal view virtual returns (uint16 code, bytes memory reason);
}
","keccak256":"0x0fa50eeb06f1af5f45ac0c6798cf2fa48aac3e0ff661bf2fa40f17fc35733e5d","license":"MIT"},"contracts/examples/OmniCounter.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "../OApp.sol";
import "../libs/OptionsBuilder.sol";
import "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroComposer.sol";

library MsgCodec {
    uint8 public constant VANILLA_TYPE = 1;
    uint8 public constant COMPOSED_TYPE = 2;
    uint8 public constant ABA_TYPE = 3;
    uint8 public constant COMPOSED_ABA_TYPE = 4;

    uint8 public constant MSG_TYPE_OFFSET = 0;
    uint8 public constant SRC_EID_OFFSET = 1;

    function encode(uint8 _type, uint32 _srcEid) internal pure returns (bytes memory) {
        return abi.encodePacked(_type, _srcEid);
    }

    function msgType(bytes calldata _message) internal pure returns (uint8) {
        return uint8(bytes1(_message[MSG_TYPE_OFFSET:SRC_EID_OFFSET]));
    }

    function srcEid(bytes calldata _message) internal pure returns (uint32) {
        return uint32(bytes4(_message[SRC_EID_OFFSET:]));
    }
}

contract OmniCounter is ILayerZeroComposer, OApp {
    using MsgCodec for bytes;
    using OptionsBuilder for bytes;

    uint public count;
    uint public composedCount;

    address public admin;
    uint32 public eid;

    mapping(uint32 => mapping(bytes32 => uint64)) public maxNonceByPath;
    bool public orderedNonce;

    // for global assertions
    mapping(uint32 => uint) public inboundCount;
    mapping(uint32 => uint) public outboundCount;

    constructor(address _endpoint) OApp(_endpoint) {
        admin = msg.sender;
        eid = endpoint.eid();
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "only admin");
        _;
    }

    // -------------------------------
    // Only Admin
    function setAdmin(address _admin) external onlyAdmin {
        admin = _admin;
    }

    function withdraw(address payable _to, uint _amount) external onlyAdmin {
        _to.transfer(_amount);
    }

    // -------------------------------
    // Send
    function increment(uint32 _eid, uint8 _type, bytes calldata _options) external payable {
        bytes memory message = MsgCodec.encode(_type, eid);
        _lzSend(_eid, message, _options, ILayerZeroEndpoint.MessagingFee(msg.value, 0), payable(msg.sender));
        _incrementOutbound(_eid);
    }

    // this is a broken function to skip incrementing outbound count
    // so that precrime will fail
    function brokenIncrement(uint32 _eid, uint8 _type, bytes calldata _options) external payable onlyAdmin {
        bytes memory message = MsgCodec.encode(_type, eid);
        _lzSend(_eid, message, _options, ILayerZeroEndpoint.MessagingFee(msg.value, 0), payable(msg.sender));
    }

    function batchIncrement(
        uint32[] calldata _eids,
        uint8[] calldata _types,
        bytes[] calldata _options
    ) external payable {
        require(_eids.length == _options.length && _eids.length == _types.length, "OmniCounter: length mismatch");

        ILayerZeroEndpoint.MessagingReceipt memory receipt;
        uint providedFee = msg.value;
        for (uint i = 0; i < _eids.length; i++) {
            address refundAddress = i == _eids.length - 1 ? msg.sender : address(this);
            bytes memory message = MsgCodec.encode(_types[i], eid);
            receipt = _lzSend(
                _eids[i],
                message,
                _options[i],
                ILayerZeroEndpoint.MessagingFee(providedFee, 0),
                payable(refundAddress)
            );
            _incrementOutbound(_eids[i]);
            providedFee -= receipt.fee.nativeFee;
        }
    }

    // -------------------------------
    // View
    function quote(
        uint32 _eid,
        uint8 _type,
        bytes calldata _options
    ) public view returns (uint nativeFee, uint zroFee) {
        return _quote(_eid, MsgCodec.encode(_type, eid), false, _options);
    }

    // -------------------------------
    // Receive
    function _lzReceive(
        MessagingContext calldata _context,
        bytes32 _guid,
        address /*_executor*/,
        bytes calldata
    ) internal override {
        uint8 messageType = _context.message.msgType();

        if (messageType == MsgCodec.VANILLA_TYPE) {
            count++;
            _incrementInbound(_context.srcEid);
        } else if (messageType == MsgCodec.COMPOSED_TYPE || messageType == MsgCodec.COMPOSED_ABA_TYPE) {
            count++;
            _incrementInbound(_context.srcEid);
            endpoint.deliverComposedMessage(address(this), _guid, _context.message);
        } else if (messageType == MsgCodec.ABA_TYPE) {
            count++;
            _incrementInbound(_context.srcEid);

            // send back to the sender
            _incrementOutbound(_context.srcEid);
            bytes memory options = OptionsBuilder.newOptions().addRelayerLzReceiveOption(200000, 0);
            _lzSend(
                _context.srcEid,
                MsgCodec.encode(MsgCodec.VANILLA_TYPE, eid),
                options,
                ILayerZeroEndpoint.MessagingFee(msg.value, 0),
                payable(address(this))
            );
        } else {
            revert("invalid message type");
        }
    }

    function _incrementInbound(uint32 _eid) internal {
        inboundCount[_eid]++;
    }

    function _incrementOutbound(uint32 _eid) internal {
        outboundCount[_eid]++;
    }

    function lzCompose(
        address /*_oapp*/,
        bytes32 /*_guid*/,
        bytes calldata _message,
        address,
        bytes calldata
    ) external payable override {
        require(msg.sender == address(endpoint), "!endpoint");

        uint8 msgType = _message.msgType();
        if (msgType == MsgCodec.COMPOSED_TYPE) {
            composedCount += 1;
        } else if (msgType == MsgCodec.COMPOSED_ABA_TYPE) {
            composedCount += 1;

            uint32 srcEid = _message.srcEid();
            _incrementOutbound(srcEid);
            bytes memory options = OptionsBuilder.newOptions().addRelayerLzReceiveOption(200000, 0);
            _lzSend(
                srcEid,
                MsgCodec.encode(MsgCodec.VANILLA_TYPE, eid),
                options,
                ILayerZeroEndpoint.MessagingFee(msg.value, 0),
                payable(address(this))
            );
        } else {
            revert("invalid message type");
        }
    }

    // -------------------------------
    // Ordered OApp
    // this demonstrates how to build an app that requires execution nonce ordering
    // normally an app should decide ordered or not on contract construction
    // this is just a demo
    function setOrderedNonce(bool _orderedNonce) external onlyOwner {
        orderedNonce = _orderedNonce;
    }

    function _acceptNonce(uint32 _srcEid, bytes32 _sender, uint64 _nonce) internal virtual override {
        uint64 currentNonce = maxNonceByPath[_srcEid][_sender];
        if (orderedNonce) {
            require(_nonce == currentNonce + 1, "OApp: invalid nonce");
        }
        // update the max nonce anyway. once the ordered mode is turned on, missing early nonces will be rejected
        if (_nonce > currentNonce) {
            maxNonceByPath[_srcEid][_sender] = _nonce;
        }
    }

    function nextNonce(uint32 _srcEid, bytes32 _sender) public view virtual override returns (uint64) {
        if (orderedNonce) {
            return maxNonceByPath[_srcEid][_sender] + 1;
        } else {
            return 0; // path nonce starts from 1. if 0 it means that there is no specific nonce enforcement
        }
    }

    // a governance function to skip nonce
    function skipInboundNonce(uint32 _srcEid, bytes32 _sender, uint64 _nonce) public virtual override onlyOwner {
        endpoint.skip(_srcEid, _sender, _nonce);
        if (orderedNonce) {
            maxNonceByPath[_srcEid][_sender]++;
        }
    }

    // be able to receive ether
    fallback() external payable {}
}
